import React from 'react';
import useCustomLogin from "../../hooks/useCustomLogin";
import KakaoLoginComponent from "./KakaoLoginComponent";

const initStates = {
    email: "",
    pw: ""
}

function LoginComponent(props) {

    const [loginParam, setLoginParam] = React.useState(initStates)

    const {doLogin, moveToPath} = useCustomLogin()

    const handleChange = (e) => {

        loginParam[e.target.name] = e.target.value

        setLoginParam({...loginParam})
    }

    const handleClickLogin = (e) => {

        // dispatch(login(loginParam))

        // 커스텀 훅 사용 후 코드
        doLogin(loginParam).then(data => {
            if(data.error){
                alert("이메일과 패스워드를 확인해 주세요")
            }else {
                moveToPath("/")
            }
        })

        // 커스텀 훅 사용 전 코드
        // dispatch(loginPostAsync(loginParam))
        //     .unwrap()   // 비동기 호출을 했지만 동기화된 것처럼 결과를 받아서 볼 수 있게 해줌
        //     .then(data => {
        //         console.log('after unwrap')
        //         console.log(data)
        //
        //         if(data.error){
        //             alert("이메일과 패스워드를 확인해 주세요")
        //         }else{
        //             alert("로그인 성공")
        //
        //             navigate({pathname:'/'}, {replace:true}) // replace:true를 하면 이전 페이지로 돌아갈 수 없다.
        //         }
        //
        //     })
    }

    return (
        <div className="border-2 border-sky-200 mt-10 m-2 p-4">
            <div className="flex justify-center">
                <div className="text-4xl m-4 p-4 font-extrabold text-blue-500">Login Component</div>
            </div>
            <div className="flex justify-center">
                <div className="relative mb-4 flex w-full flex-wrap items-stretch">
                    <div className="w-full p-3 text-left font-bold">Email</div>
                    <input className="w-full p-3 rounded-r border border-solid border-neutral-500 shadow-md"
                           name="email"
                           type={'text'}
                           value={loginParam.email}
                           onChange={handleChange}
                           >
                    </input>
                </div>
            </div>
            <div className="flex justify-center">
                <div className="relative mb-4 flex w-full flex-wrap items-stretch">
                    <div className="w-full p-3 text-left font-bold">Password</div>
                    <input className="w-full p-3 rounded-r border border-solid border-neutral-500 shadow-md"
                           name="pw"
                           type={'password'}
                           value={loginParam.pw}
                           onChange={handleChange}
                           >
                    </input>
                </div>
            </div>
            <div className="flex justify-center">
                <div className="relative mb-4 flex w-full justify-center">
                    <div className=" w-2/5 p-6 flex justify-center font-bold">
                        <button
                            className="rounded p-4 w-36 bg-blue-500 text-xl text-white"
                            onClick={handleClickLogin}
                            >
                            Login
                        </button>
                    </div>
                </div>
            </div>
            <KakaoLoginComponent/>
        </div>
    );
}

export default LoginComponent;
