import React, {useState} from 'react';
import axios from "axios";
import BasicMenu from "../components/menus/BasicMenu";
import {Link} from "react-router-dom";

const MainPage = () => {

    const [targetDT, setTargetDT] = useState("");
    const [top10Movies, setTop10Movies] = useState([]);

    const handleChangeTarget = (e) => {
        setTargetDT(e.target.value)
    }

    const handleClick = () => {
        axios.get(
            `http://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=f5eef3421c602c6cb7ea224104795888&targetDt=${targetDT}`
        ).then((res) => {
            const list = res.data.boxOfficeResult.dailyBoxOfficeList;
            const movies = list
                .filter((movie, index) => index < 10)
                .map((movie) => movie.movieNm);
            setTop10Movies(movies);
        }).catch((err) => {
            console.log(err);
        });
    }

    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col">
            {/* Header */}
            <header className="bg-white shadow-md py-4 px-6">
                <div className="flex items-center justify-between">
                    <h1 className="text-2xl font-bold text-gray-800" >
                        <Link to={'/'}> Code </Link></h1>
                    <button
                        className="text-gray-600 hover:text-gray-800 focus:outline-none">
                        <svg
                            className="h-6 w-6"
                            fill="none"
                            viewBox="0 0 24 24"
                            stroke="currentColor"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth={2}
                                d="M4 6h16M4 12h16M4 18h16"
                            />
                        </svg>
                    </button>
                </div>
            </header>

            {/* Content */}
            <main className="flex-grow py-6 px-4">
                <div className="bg-white rounded-lg shadow-md p-4">
                    <h2 className="text-lg font-semibold mb-4">Welcome to Code</h2>
                    <p className="text-gray-700 mb-4">
                        어제보다 나은 오늘을 위해
                        <br/>
                        오늘보다 나은 내일을 위해
                    </p>
                    <button className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded">
                        문제 풀러 가기
                    </button>
                </div>
            </main>

            {/* Footer */}
            <BasicMenu/>

            {/*<footer className="bg-gray-800 py-4 px-6">*/}
            {/*    <p className="text-sm text-gray-300">*/}
            {/*        &copy; 2024 My App. All rights reserved.*/}
            {/*    </p>*/}
            {/*</footer>*/}
        </div>
    );
}

export default MainPage;
