// src/layouts/BasicHeader.js
import React, {useState} from 'react';
import { Link } from 'react-router-dom';

const BasicHeader = () => {

    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    return (
        <>
            {/* Header */}
            <header className="bg-white shadow-md py-4 px-6">
                <div className="flex items-center justify-between">
                    <h1 className="text-2xl font-bold text-gray-800">
                        <Link to={'/'}>Code</Link>
                    </h1>
                    <button
                        className="text-gray-600 hover:text-gray-800 focus:outline-none"
                        onClick={toggleSidebar}
                    >
                        <svg
                            className="h-6 w-6"
                            fill="none"
                            viewBox="0 0 24 24"
                            stroke="currentColor"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth={2}
                                d="M4 6h16M4 12h16M4 18h16"
                            />
                        </svg>
                    </button>
                </div>
            </header>

            {/* Sidebar */}
            <div
                className={`fixed opacity-90 inset-y-0 left-0 z-40 w-64 bg-gray-800 transition-transform duration-300 transform ${
                    isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
                }`}>
                <div className="py-4 px-6">
                    {/* Sidebar content */}
                    <div className="flex flex-col space-y-4">
                        <Link to="/" className="text-white hover:text-gray-300">
                            Main
                        </Link>
                        <Link to="/about" className="text-white hover:text-gray-300">
                            About
                        </Link>
                        <Link to="/community" className="text-white hover:text-gray-300">
                            Community
                        </Link>
                        <Link to="/todo" className="text-white hover:text-gray-300">
                            Todo
                        </Link>
                    </div>
                </div>
            </div>
        </>
    );
};

export default BasicHeader;
