import React, {useEffect, useState} from 'react';
import axios from "axios";

const MainPage = () => {

    const [targetDT, setTargetDT] = useState("");
    const [list, setList] = useState([]);
    const [search, setSearch] = useState("");

    const handleChangeTarget = (e) => {

        setTargetDT(e.target.value)

    }

    useEffect(() => {
        axios.get(
            `http://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=f5eef3421c602c6cb7ea224104795888&targetDt=${targetDT}`
        ).then((res) => {
            const list = res.data.boxOfficeResult.dailyBoxOfficeList;
            const top10Movies = list
                .filter(movie => movie.rank <= 10)
                .map(movie => movie.movieNM);
            console.log(top10Movies);
        }).catch((err) => {
            console.log(err);
        });
    }, [targetDT]);

    return (
        <>

            <div className="text-2xl">
                날짜별 영화 랭킹 사이트
            </div>
            <div className="text-xl">
                날짜 입력 (yyyymmdd) :
                <input type="text"
                className="border-2 border-gray-300 justify-center"
                    placeholder="yyyymmdd"
                       value={ targetDT }
                       onChange={handleChangeTarget}
            />
            </div>
            <div>

            </div>

        </>

    );
}

export default MainPage;
