import React from 'react';

function PageComponent({serverData, movePage}) {
    console.log(serverData.current)

    //serverData.prev, pageNumList, next
    return (
        <div className="m-6 flex justify-center">

            {serverData.prev ?
            <div
                className="ml-2 p-2 w-16 text-center font-bold text-blue-400"
                onClick={() => movePage({page:serverData.prevPage})}>
                prev </div> : <></>}

            {serverData.pageNumList.map(pageNum =>
                <div
                    key={pageNum}
                    className={`w-8 m-2 p-2 text-center rounded shadow-md text-white ${serverData.current === pageNum? 'bg-gray-500' : 'bg-blue-400'}`}
                    onClick={() => movePage( {page:pageNum})}>
                    {pageNum}
                </div>
            )}

            {serverData.next ?
                <div
                    className="m-2 p-2 w-16 text-center font-bold text-blue-400"
                    onClick={() => movePage({page:serverData.nextPage})}>
                    next
                </div> : <></>}

        </div>
    );
}

export default PageComponent;
